    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        String path = "jdbc:mysql://localhost:3306/";
        String place = "project1";


        if (jComboBox1.getSelectedIndex() != 0) {
            try {
                Class.forName("com.mysql.jdbc.Driver");
                Connection myconnection = DriverManager.getConnection(path + place, "root", "");
                try {
                    String q = "select producttype from addstock where comapnyname=?";
                    PreparedStatement mystatement = myconnection.prepareStatement(q);
                    mystatement.setString(1, jComboBox1.getSelectedItem().toString());


                    ResultSet myresult = mystatement.executeQuery();
                    jComboBox2.removeAllItems();
                    if (myresult.next()) {
                        jComboBox2.addItem("Choose product");
                        do {
                            jComboBox2.addItem(myresult.getString("producttype"));
                        } while (myresult.next());
                    } else {
                        JOptionPane.showMessageDialog(rootPane, "no product type found");
                    }


                    mystatement.close();
                    myconnection.close();
                } catch (Exception e) {
                    //System.out.println("Error in query"+e.getMessage());
                    JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
                }
            } catch (Exception e) {
                //System.out.println("Error on connnection"+e.getMessage());
                JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
            }
        }
    }//GEN-LAST:event_jComboBox1ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String path = "jdbc:mysql://localhost:3306/";
        String place = "project1";
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection myconnection = DriverManager.getConnection(path + place, "root", "");
            try {
                String q = "select * from newstock1 where no=?";
                PreparedStatement mystatement = myconnection.prepareStatement(q);
                mystatement.setString(1, num.getText());


                ResultSet myresult = mystatement.executeQuery();

                if (myresult.next()) {

                    String dob = myresult.getString("namebox");
                    //String a[]=dob.split("");
                    jComboBox1.setSelectedItem(dob);
                    String dob1 = myresult.getString("stocknamebox");
                    // String ab[]=dob1.split("");
                    jComboBox2.setSelectedItem(dob1);
                    amountbox.setText(myresult.getString("amountbox"));
                    quanbox.setText(myresult.getString("quantitybox"));

                } else {
                    JOptionPane.showMessageDialog(rootPane, "no product available");
                }


                mystatement.close();
                myconnection.close();
            } catch (Exception e) {
                //System.out.println("Error in query"+e.getMessage());
                JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
            }
        } catch (Exception e) {
            //System.out.println("Error on connnection"+e.getMessage());
            JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
        }


    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        String path = "jdbc:mysql://localhost:3306/";
        String place = "project1";

        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection myconnection = DriverManager.getConnection(path + place, "root", "");
            try {
                String q = "update newstock1 set quantitybox=? where no=?";
                PreparedStatement mystatement = myconnection.prepareStatement(q);
                double ttl = Double.parseDouble(quanbox1.getText()) + Double.parseDouble(quanbox.getText());
                mystatement.setString(1, ttl + "");
                mystatement.setString(2, num.getText());
                mystatement.executeUpdate();
                JOptionPane.showMessageDialog(rootPane, "Updated Successfully");
                mystatement.close();
                myconnection.close();
            } catch (Exception e) {
                //jLabel1.setText("Error in query"+e.getMessage());
                JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
            }
        } catch (Exception e) {
            //jLabel1.setText("Error on connnection"+e.getMessage());
            JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());

        }
        num.setText(null);
        amountbox.setText(null);
        quanbox.setText(null);
        quanbox1.setText(null);
        jComboBox1.setSelectedIndex(0);
        jComboBox2.setSelectedIndex(0);

    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String path = "jdbc:mysql://localhost:3306/";
        String place = "project1";
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection myconnection = DriverManager.getConnection(path + place, "root", "");
            try {
                String q = "select * from newstock1 where namebox=? and stocknamebox=?";
                PreparedStatement mystatement = myconnection.prepareStatement(q);
                mystatement.setString(1, jComboBox1.getSelectedItem().toString());
                mystatement.setString(2, jComboBox2.getSelectedItem().toString());


                ResultSet myresult = mystatement.executeQuery();

                if (myresult.next()) {
                    num.setText(myresult.getString("no"));
                    amountbox.setText(myresult.getString("amountbox"));
                    quanbox.setText(myresult.getString("quantitybox"));
                    jButton2.setEnabled(true);
                } else {

                    num.setText("");
                    amountbox.setText("");
                    quanbox.setText("");
                    jButton2.setEnabled(false);

                    JOptionPane.showMessageDialog(rootPane, "no product available");
                }


                mystatement.close();
                myconnection.close();
            } catch (Exception e) {
                //System.out.println("Error in query"+e.getMessage());
                JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
            }
        } catch (Exception e) {
            //System.out.println("Error on connnection"+e.getMessage());
            JOptionPane.showMessageDialog(rootPane, "Error in query" + e.getMessage());
        }        // TODO add your handling code here:
    }//GEN-LAST:event_jButton3ActionPerformed
